SnippetCode.val:
using System;
using System.Windows.Forms;


public static class Snippet
{
	public static void func(System.String p_session, System.String p_path, System.String p_type, System.Int32 p_type_def, System.String p_names)

	{ 
		OpenFileDialog openDialog = new OpenFileDialog();
		openDialog.Title = "Select File";
		openDialog.InitialDirectory = p_path;
		openDialog.Filter = p_type;
		//"Text Files (*.txt)|*.txt" + "|" + 
        //		            "Image Files (*.png;*.jpg)|*.png;*.jpg" + "|" +
        //        		    "All Files (*.*)|*.*";
		openDialog.FilterIndex = p_type_def;
		openDialog.CheckFileExists = true;
		openDialog.CheckPathExists = true;
		openDialog.Multiselect = false;
		if (openDialog.ShowDialog() == DialogResult.OK)
		{
			// Read the files
        	foreach (String file in openDialog.FileNames) 
        	{
            	// Append
				p_names = file;
			}
			System.IO.File.WriteAllText(string.Format(@"c:\apl\aquaclaro\temp\fileselect{0}.txt",p_session), p_names);
		}
    return;  
	}  		

}
