SnippetCode.val:
using System;
using System.Net.Mail;

public static class Snippet
{
	public static void func(System.String p_to, System.String p_cc, System.String p_bcc, System.String p_from, System.String p_password, System.String p_fromname, System.String p_subject, System.String p_body, System.String p_attachments)
    {
        SmtpClient SmtpServer = new SmtpClient(); //Defines new SMTP server (i.e - Gmail)
        SmtpServer.Credentials = new System.Net.NetworkCredential(p_from, p_password); //Your connection details - User Name & Password
        SmtpServer.Port = 587; // Gmail out port
        SmtpServer.Host = "smtp.office365.com"; // Gmail host address
        SmtpServer.EnableSsl = true; //Sets the server to be SSL
        MailMessage mail = new MailMessage(); // Creates new Mail message
        mail.From = new MailAddress(p_from); //Sets From address without display name
        mail.Subject = p_subject; //Sets the subject
        mail.Body = p_body; //Sets the body (can be HTML text)
        mail.IsBodyHtml = true; // You need to set IsBodyHtml property to true if you want that the body will be HTML text
        //mail.BodyEncoding = System.Text.Encoding.UTF8;
        try
        {
            if (p_to != "") mail.To.Add(p_to); //Sets To parameter. Can't be empty.
            if (p_cc != "") mail.CC.Add(p_cc); //Sets CC parameter. Can't be empty.
            if (p_bcc != "") mail.Bcc.Add(p_bcc); //Sets BCC parameter. Can't be empty.
            if (p_attachments != "")
            { //Checks that the attachments are not empty
                string[] attfiles = p_attachments.Split(',');
                foreach (var attfile in attfiles)
                {
                    Attachment attachment = new Attachment(attfile); //Creates new Attachment
                    mail.Attachments.Add(attachment); // Adds the attachment to the Mail message
                }
            }

        SmtpServer.Send(mail); //Sends the Email
        //status.Text = "message was sent successfully";
        } catch (System.FormatException) {}
            //status.Text = ex.StackTrace;


        }

}

